import { Container } from 'react-bootstrap';
import './App.css';
import Header from './components/Header';
import React, { useState, useEffect } from 'react'
import { Form, Row, Col, Button, Card, Image } from 'react-bootstrap';
import axios from "axios";
import TextField from '@mui/material/TextField';

function App() {
    const [listWeather, setListWeather] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [dateTime, setDateTime] = useState('');

    const handleSearch = async (e) => {
        if (e.key === 'Enter' || e.target.type === 'submit') {
            const response = await axios.post("http://localhost/GOLDEN%20OWL/Back-end/weatherApi.php?search=" + searchTerm)
            setListWeather(response.data);
        }
    };

    const fetchWeatherData = async (searchTerm) => {
        try {
            const response = await axios.post(`http://localhost/GOLDEN%20OWL/Back-end/weatherApi.php?search=${searchTerm}`);
            setListWeather(response.data);
        } catch (error) {
            console.error("Error fetching weather data:", error);
        }
    };

    useEffect(() => {
        fetchWeatherData("London");
    }, []);

    useEffect(() => {
        console.log(listWeather); // Log sau khi state được cập nhật
        // const dateTimeString = listWeather.location?.localtime;
        // setDateTime(dateTimeString.split(" "));
        if (listWeather && listWeather.location && listWeather.location.localtime) {
            const dateTimeString = listWeather.location?.localtime;
            setDateTime(dateTimeString.split(" "));
            // console.log(typeof dateTimeString);
        }
    }, [listWeather]);


    return (
        <div className="app-container">
            <Header />
            <div style={{ backgroundColor: '#e3f2fd', padding: '30px 0 0 0', margin: '0', width: '100%' }}>
                <Row>
                    <Col lg={4} md={4}>
                        <div style={{
                            width: '70%',
                            marginLeft: '25px',
                        }}>
                            <h5>Enter a City Name</h5>
                            <TextField
                                id="outlined-basic"
                                label="E.g., New York, London, Tokyo"
                                variant="outlined"
                                value={searchTerm}
                                onChange={e => setSearchTerm(e.target.value)}
                                onKeyDown={handleSearch}
                                style={{ marginBottom: '10px', width: '100%' }}
                            />
                            <Button
                                variant="primary"
                                type="submit"
                                onClick={handleSearch}
                                style={{ marginTop: '10px', backgroundColor: '#5372f0', color: '#fff', width: '100%' }}
                            >
                                Search
                            </Button>
                            <hr style={{ border: 'none', height: '1px', backgroundColor: '#ccc', margin: '20px 0' }} />
                            <Button
                                variant="secondary"
                                type="submit"
                                style={{ marginTop: '10px', backgroundColor: '#6c757d', color: '#fff', width: '100%' }}
                            >
                                Use Current Location
                            </Button>
                        </div>
                    </Col>
                    <Col lg={7} md={8}>
                        <Row>
                            <Card style={{ padding: '0', width: '100%', borderRadius: '5px', backgroundColor: '#5372f0', color: '#fff' }}>
                                <Card.Body style={{
                                    display: 'flex',
                                    justifyContent: 'space-between'
                                }}>
                                    <div>
                                        {/* {listWeather.map((item, index) => (
                                            // <tr key={index}>
                                            //     <td>{item.id}</td>
                                            //     <td>{item.email}</td>
                                            //     <td>{item.first_name}</td>
                                            //     <std>{item.last_name}</std>
                                            // </tr>
                                            <p></p>
                                        ))} */}
                                        <Card.Title>{listWeather.location?.name} {dateTime}</Card.Title>
                                        <Card.Text style={{
                                            marginBottom: '5px'
                                        }}>
                                            Temperature: 18.71 C
                                        </Card.Text>
                                        <Card.Text style={{
                                            marginBottom: '5px'
                                        }}>
                                            Wind: 4.31 M/S
                                        </Card.Text>
                                        <Card.Text>
                                            Humidity: 76%
                                        </Card.Text>
                                    </div>
                                    <div style={{
                                        width: '15%',
                                        alignItems: 'stretch',
                                        display: 'flex',
                                        flexDirection: 'column',
                                        justifyContent: 'center'
                                    }}>
                                        <Image style={{
                                            width: '80px',
                                            height: '80px'
                                        }} src="https://cdn.weatherapi.com/weather/64x64/day/116.png" rounded />
                                        <span style={{
                                            fontSize: '14px'
                                        }}>Moderate rain</span>
                                    </div>
                                </Card.Body>
                            </Card>
                        </Row>
                        <Row>
                            <div style={{
                                height: '50px',
                                marginTop: '20px',
                                padding: '0'
                            }}>
                                <h3>4-Day Forecast</h3>
                            </div>
                            <Col lg={3}>
                                <Card style={{ width: '100%', borderRadius: '5px', backgroundColor: '#6c757d', color: '#fff' }}>
                                    <Card.Body>
                                        <Card.Title>(2023-06-19)</Card.Title>
                                        <Image src="https://cdn.weatherapi.com/weather/64x64/day/116.png" rounded />
                                        <Card.Text style={{
                                            marginBottom: '5px'
                                        }}>
                                            Temperature: 18.71 C
                                        </Card.Text>
                                        <Card.Text style={{
                                            marginBottom: '5px'
                                        }}>
                                            Wind: 4.31 M/S
                                        </Card.Text>
                                        <Card.Text>
                                            Humidity: 76%
                                        </Card.Text>
                                    </Card.Body>
                                </Card>
                            </Col>
                        </Row>
                    </Col>
                </Row>
            </div >
        </div >
    );
}

export default App;
