                                        <Card.Text style={{
                                            marginBottom: '5px'
                                        }}>
                                            Wind: {wine} M/S
                                        </Card.Text>
                                        <Card.Text>
                                            Humidity: {listWeather.current?.humidity}%
                                        </Card.Text>
                                    </div>
                                    <div style={{
                                        width: '15%',
                                        alignItems: 'center',
                                        display: 'flex',
                                        flexDirection: 'column',
                                        justifyContent: 'center'
                                    }}>
                                        <Image style={{
                                            width: '80px',
                                            height: '80px'
