import { Container } from 'react-bootstrap';
import './App.css';
import Header from './components/Header';
import React, { useState, useEffect } from 'react'
import { Form, Row, Col, Button, Card, Image } from 'react-bootstrap';
import axios from "axios";
import TextField from '@mui/material/TextField';

function App() {
    const [listWeather, setListWeather] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [dateTime, setDateTime] = useState('');
    const [wine, setWine] = useState(0);

    const handleSearch = async (e) => {
        if (e.key === 'Enter' || e.target.type === 'submit') {
            const response = await axios.post("http://localhost/GOLDEN%20OWL/Back-end/weatherApi.php?search=" + searchTerm)
            setListWeather(response.data);
        }
    };

    const fetchWeatherData = async (searchTerm) => {
        try {
            const response = await axios.post(`http://localhost/GOLDEN%20OWL/Back-end/weatherApi.php?search=${searchTerm}`);
            setListWeather(response.data);
        } catch (error) {
            console.error("Error fetching weather data:", error);
        }
    };

    useEffect(() => {
        fetchWeatherData("London");
    }, []);

    useEffect(() => {
        console.log(listWeather);
        if (listWeather && listWeather.location && listWeather.location.localtime) {
            const dateTimeString = listWeather.location?.localtime;
            setDateTime(dateTimeString.split(" "));
        }
        if (listWeather && listWeather.current && listWeather.current.wind_kph) {
            const initWine = listWeather.current?.wind_kph;
            setWine(((initWine * 1000) / 3000).toFixed(2));
        }

    }, [listWeather]);

    const WeatherForecast = ({ forecast }) => {
        // Kiểm tra xem forecast có tồn tại và là một mảng không trước khi sử dụng map
        if (!forecast || !Array.isArray(forecast)) {
            return <p>Không có dữ liệu dự báo thời tiết</p>;
        }
        return (
            <div>
                {forecast.map((item, index) => (
                    <Col lg={3} key={index}>
                        <Card style={{ width: '100%', borderRadius: '5px', backgroundColor: '#6c757d', color: '#fff' }}>
                            <Card.Body>
                                <Card.Title>({index.date})</Card.Title>
                                <Image src="https://cdn.weatherapi.com/weather/64x64/day/116.png" rounded />
                                <Card.Text style={{
                                    marginBottom: '5px'
                                }}>
                                    Temperature: {index.avgtemp_c} °C
                                </Card.Text>
                                <Card.Text style={{
                                    marginBottom: '5px'
                                }}>
                                    Wind: {index.avgtemp_c} M/S
                                </Card.Text>
                                <Card.Text>
                                    Humidity: {index.avghumidity}%
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </div>
        );
    };


    return (
        <div className="app-container">
            <Header />
            <div style={{ backgroundColor: '#e3f2fd', padding: '30px 0 0 0', margin: '0', width: '100%' }}>
                <Row>
                    <Col lg={4} md={4}>
                        <div style={{
                            width: '70%',
                            marginLeft: '25px',
                        }}>
                            <h5>Enter a City Name</h5>
                            <TextField
                                id="outlined-basic"
                                label="E.g., New York, London, Tokyo"
                                variant="outlined"
                                value={searchTerm}
                                onChange={e => setSearchTerm(e.target.value)}
                                onKeyDown={handleSearch}
                                style={{ marginBottom: '10px', width: '100%' }}
                            />
                            <Button
                                variant="primary"
                                type="submit"
                                onClick={handleSearch}
                                style={{ marginTop: '10px', backgroundColor: '#5372f0', color: '#fff', width: '100%' }}
                            >
                                Search
                            </Button>
                            <hr style={{ border: 'none', height: '1px', backgroundColor: '#ccc', margin: '20px 0' }} />
                            <Button
                                variant="secondary"
                                type="submit"
                                style={{ marginTop: '10px', backgroundColor: '#6c757d', color: '#fff', width: '100%' }}
                            >
                                Use Current Location
                            </Button>
                        </div>
                    </Col>
                    <Col lg={7} md={8}>
                        <Row>
                            <Card style={{ padding: '0', width: '100%', borderRadius: '5px', backgroundColor: '#5372f0', color: '#fff' }}>
                                <Card.Body style={{
                                    display: 'flex',
                                    justifyContent: 'space-between'
                                }}>
                                    <div>
                                        <Card.Title>{listWeather.location?.name} {dateTime[0]}</Card.Title>
                                        <Card.Text style={{
                                            marginBottom: '5px'
                                        }}>
                                            Temperature: {listWeather.current?.temp_c} °C
                                        </Card.Text>
                                        <Card.Text style={{
                                            marginBottom: '5px'
                                        }}>
                                            Wind: {wine} M/S
                                        </Card.Text>
                                        <Card.Text>
                                            Humidity: {listWeather.current?.humidity}%
                                        </Card.Text>
                                    </div>
                                    <div style={{
                                        width: '15%',
                                        alignItems: 'stretch',
                                        display: 'flex',
                                        flexDirection: 'column',
                                        justifyContent: 'center'
                                    }}>
                                        <Image style={{
                                            width: '80px',
                                            height: '80px'
                                        }} src="{listWeather.condition.icon}" rounded />
                                        <span style={{
                                            fontSize: '14px'
                                        }}>Moderate rain</span>
                                    </div>
                                </Card.Body>
                            </Card>
                        </Row>
                        <Row>
                            <div style={{
                                height: '50px',
                                marginTop: '20px',
                                padding: '0'
                            }}>
                                <h3>4-Day Forecast</h3>
                            </div>
                            <WeatherForecast forecast={listWeather.forecast} />
                        </Row>
                    </Col>
                </Row>
            </div >
        </div >
    );
}

export default App;
